settings.custom_css:
== {
margin-top: 20px;
}
== .two-grid{
display: grid;
grid-template-columns: 1fr 1fr;
background:#CDC4B8;
align-items: center;
}
== .feature-article-content{
padding: 30px 60px;
margin: auto;
}
== .feature-article-image,
== img{
max-height: 450px;
object-fit:cover;
height: 100%;
}
== h3{
font-size: 32px;
margin-bottom: 15px;
}
== .tag{
font-size:16px;
}
== article{
margin-top: 15px;
margin-bottom: 10px;
font-size: 15px;
}
/medium-down/{
== .two-grid{
grid-template-columns: 1fr;
}
}
